DROP TABLE IF EXISTS `contracts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `contracts` (
  `id` varchar(50) NOT NULL,
  `client_id` varchar(255) NOT NULL,
  `client_type` enum('فرد','منشأة') NOT NULL,
  `service_id` int DEFAULT NULL,
  `service_name` varchar(200) DEFAULT NULL,
  `from_date` date DEFAULT NULL,
  `to_date` date DEFAULT NULL,
  `container_type` varchar(80) DEFAULT NULL,
  `container_location` varchar(64) DEFAULT NULL,
  `pickup_location` varchar(64) DEFAULT NULL,
  `location` varchar(255) DEFAULT NULL,
  `monthly_fee` decimal(12,2) DEFAULT '0.00',
  `total_price` decimal(12,2) DEFAULT '0.00',
  `manager` varchar(150) DEFAULT NULL,
  `status` enum('active','pending','expired') DEFAULT 'pending',
  `sign_method` varchar(50) DEFAULT 'manual',
  `terms` text,
  `extra` text,
  `pdf_url` varchar(512) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `signature_data` json DEFAULT NULL,
  `signed_at` timestamp NULL DEFAULT NULL,
  `signed_ip` varchar(45) DEFAULT NULL,
  `signed_by` varchar(100) DEFAULT NULL,
  `file_url` varchar(255) DEFAULT NULL,
  `client_email` varchar(255) DEFAULT NULL,
  `city` varchar(100) DEFAULT NULL,
  `district` varchar(100) DEFAULT NULL,
  `duration` varchar(50) DEFAULT NULL,
  `collector` varchar(150) DEFAULT NULL,
  `client_phone` varchar(50) DEFAULT NULL,
  `first_party` varchar(255) DEFAULT 'شركة بفت للمقاولات',
  `second_party` varchar(255) DEFAULT NULL,
  `payment_type` varchar(50) DEFAULT 'سنوية',
  `tax` varchar(50) DEFAULT '15%',
  `parent_contract_id` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `contracts_ibfk_1` (`client_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `contracts`
