INSERT INTO `contracts` VALUES ('CN-2025-341','13','منشأة',NULL,'نظافة أسبوعية','2025-11-17','2025-11-26','برميل','18.222443,42.561302','18.222443113536585, 42.56130221625266','أبها',10.00,120.00,'زين العابدين','expired','إلكتروني','البند: قيمة العقد و الدفع\nقيمة هذا العقد شهريا قبل الضريبة بمبلغ (۱۰۰) فقط...','',NULL,'2025-11-18 08:21:10','2025-12-05 17:20:20',NULL,NULL,NULL,NULL,NULL,'zosana333@gmail.com','Abha','المنسك','سنوي','مصعب','1271871373','شركة بفت للمقاولات','مثلا مثلا','سنوية','15%',NULL),('CN-2025-526','6','منشأة',NULL,'نظافة أسبوعية','2024-09-17','2025-09-17','برميل','18.206070,42.516793',NULL,NULL,100.00,1380.00,'زين العابدين','expired','إلكتروني','البند الأول :\n<p class=\"ql-align-right\"><strong style=\"font-size: 20px;\">نموذج عقد\n</strong>\n<strong>١٥ / ١١ / ٢٠٢٥م\n</strong>\nمحصل / على محسن سوق / على محسن\n\nعقد جديد\n\nعقد اتفاق نقل النفايات التجارية\n\nالبند الأولية\n\nرقم العقد (3356)\n\nعنوانها: (حجلة ) إشارة باحص خلف مجموعة الجربوع\n\nالسجل التجاري رقم : ۵۸۵۰۰۱۹۹۸۹ ) جوال / ٠٥٥٢۲۰۰۹۳۱/۰۰۳۷۷۷۱۱۰۹\n\nالطرف الأول: شركة بفت المقاولات هاتف رقم : ۲۲۷٨٦٦٦-۱۷ - تحويلة (۱۰۰\n\nالطرف الثاني: مؤسسه مفهوم الذوق للتجاره\n\nبريد الكتروني : BAFT-EST @HOTMAIL.COM\n\nسجل تجاري رقم (٥٨٥٥٣٧١٢٦٣) الرقم الموحد (٧٠٣٧٩٧٤٥٧٨).\n\nويمثلها في هذا العقد : جابر مفرح على هروبي ) السجل المدني (۱۰۰۳٠٨۲۳۹۹) ، جوال رقم جوال رقم (۰۰۷۸۷٠۱۰۰۷)\n\nالعنوان : احد رفيده / حي الزهور ) رقم الرخصة (-) الرقم الضريبي (-).\n\nوبعد أن أقر الطرفان بأهليتهما الشرعية والنظامية للتعاقد والتصرف اللقا على الآتي:\n1- يقوم الطرف الأول بنقل وترحيل ما ينتجه الطرف الثاني من نقابات بواسطة عماله والياته ومعناته والتخلص منها بترحيلها إلى المرمى العام.\n\nيدفع الطرف الثاني للطرف الأول أجرة هذه الخدمة بداية كل شهر مقدما حسب التاريخ الموضوحيلتزم الطرف الأول بتأمين حاوية مقاس (مشترك) ونقل ما بداخل الحاوية فقط .\n\nيلتزم الطرف الثاني بوضع ما ينتجه من نفايات في أكياس محكمة وربطها جيدا لمنع ترب المواد السائلة البلد الثاني : قيمة العقد\n\nعند وضعها أو نقلها وذلك حفاظا على النظافة العامة وصحة البيئة .\n\nقيمة هذا العقد شهريا قبل الضريبة بمبلغ (۱۰۰) فقط مائة ريال ، بالإضافة الى قيمة الضريبة المضافة (١٥) بمبلغ (٥))\n\nفقط خمسة عشرة ريال بإجمالي شهري مع ضريبة القيمة المضافة (۱۱ۥ) فقط مائة وخمسة عشرة ريال شهري\n\nالبند الثالث : مدة العقد\n\nعلما بأن شركة بقت المقاولات مسجلة في ضريبة القيمة المضافة بالرقم الضريبي (۳۱۲۱۹۷٥۴۲۷۰۰۰۰۳)\n\nهدة هذا العقد (سنة) من تاريخ ۲۰۲۵/۱۱/۰۱م يتجدد تلقائياً مالم يشعر أي من الطرفين الطرف الآخر بعدم رغبته في التجد قبل شهر على الأقل من نهاية مدة العقد بناء على البيانات السابقة في عناوين الطرفين\n\nحب على الطرف الثاني ابلاغ الطرف الأول بأي ملاحظات عن اداء العمل بموجب إشعار كتابي يرسل على إحدى العناوين السابقة . يكون سداد المبالغ المالية بين الطرفين الأول بالي والتحية للشركة وخلاف ذلك فإن الطرف الأول غير مسؤول عن ذلك تم تحرير هذا العقد من نسختين لكل من الطرفين نسخة، ولكم جزيل الشكر .\n</p><p class=\"ql-align-right\">\n\nملاحظه / العمر لا يعتمد الا بالختم</p>','',NULL,'2025-11-04 23:02:22','2025-12-05 17:20:20',NULL,NULL,NULL,NULL,NULL,'zosana333@gmail.com','Abha','نزهة','سنوي',NULL,'01103466550','شركة بفت للمقاولات','eeee','سنوية','15%',NULL),('CN-2025-943','client-1763794643104','فرد',NULL,'نظافة يومية','2025-10-26','2025-11-09','6 ياردة','18.208539,42.510910','18.208539,42.510910','n',100.00,1200.00,'n','expired','إلكتروني','3333\nنموذج عقد\n\n١٥ / ١١ / ٢٠٢٥م\n\nمحصل / على محسن سوق / على محسن\n\nعقد جديد\n\nعقد اتفاق نقل النفايات التجارية\n\nالبند الأولية\n\nرقم العقد (3356)\n\nعنوانها: (حجلة ) إشارة باحص خلف مجموعة الجربوع\n\nالسجل التجاري رقم : ۵۸۵۰۰۱۹۹۸۹ ) جوال / ٠٥٥٢۲۰۰۹۳۱/۰۰۳۷۷۷۱۱۰۹\n\nالطرف الأول: شركة بفت المقاولات هاتف رقم : ۲۲۷٨٦٦٦-۱۷ - تحويلة (۱۰۰\n\nالطرف الثاني: مؤسسه مفهوم الذوق للتجاره\n\nبريد الكتروني : BAFT-EST @HOTMAIL.COM\n\nسجل تجاري رقم (٥٨٥٥٣٧١٢٦٣) الرقم الموحد (٧٠٣٧٩٧٤٥٧٨).\n\nويمثلها في هذا العقد : جابر مفرح على هروبي ) السجل المدني (۱۰۰۳٠٨۲۳۹۹) ، جوال رقم جوال رقم (۰۰۷۸۷٠۱۰۰۷)\n\nالعنوان : احد رفيده / حي الزهور ) رقم الرخصة (-) الرقم الضريبي (-).\n\nوبعد أن أقر الطرفان بأهليتهما الشرعية والنظامية للتعاقد والتصرف اللقا ع