INSERT INTO `terms` VALUES (12,'الطرف الثاني :','text','<p style=\"direction: ltr;\" class=\"ql-align-right\"><span style=\"font-size: 14px;\">‏</span><span dir=\"rtl\"><strong>الطرف الثاني /</strong> <strong>hp 2 test 11111</strong>، وعنوانها في أبها - المنسك , البريد الإلكتروني (<span dir=\"ltr\" style=\"display:inline-block; text-align:right;\">zosana333@gmail.com</span>) : جوال ( 01103466550 ) ويمثله في التواقيع على هذا العقد الاستاذ / fukk - بصفته shit ونشار اليه في هذا العقد ب(الطرف الثاني)</span></span><span class=\"ql-cursor\">﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿﻿</span></p>','2025-12-08 22:48:37','2026-01-04 23:12:13'),(13,'تمهيد','text','<p class=\"ql-align-center\"><strong style=\"font-size: 14px;\">تمهید :</strong></p><p class=\"ql-align-right\"><span style=\"font-size: 14px;\">لما كان الطرف الأول عليه الخبرة والدراية والإمكانيات اللازمة ولديه اطلاع عام على الأنظمة المعمول بها في المملكة العربية السعودية، ولكونه أحد الجهات المتخصصة في توريد الحاويات فقد أبدى استعداده التام للقيام بتوريد الحاويات وتفريغها بشكل مستمر وذلك من موقع الطرف الثاني الكائن في خميس مشيط (مركز صيانة الشركة الخليجية) إلى المرمى العام، لذا فقد اتفق الطرفان وهما بكامل أهليتهما المعتبرة شرعاً ونظاما على ما يلي -</span></p>','2025-12-08 22:49:49','2026-01-04 12:15:20'),(14,'البند الأول نطاق العمل والمسؤوليات والغرامات الطرفين:','text','<p><strong style=\"font-size: 14px;\"><u>البند الأول نطاق العمل والمسؤوليات والغرامات الطرفين:-</u></strong></p><p><span style=\"font-size: 14px;\">1- يقوم الطرف الأول ينقل وترحيل ما ينتجه الطرف الثاني من نقابات بواسطة عماله وآلياته ومعداته والتخلص منها بترحيلها إلى المرمى العام، على أن يكون ذلك بشكل يومي طوال أيام الأسبوع وخلال ساعات العمل وبشكل نظامي .</span></p><p><span style=\"font-size: 14px;\">2- يلتزم الطرف الأول بعدم التأخر في نقل و ترحيل أياً من النقابات وأن يلتزم بالحضور بشكل يومي إلى مقر الطرف الثاني، وفي حال تخلف عن الحضور لأي سبب كان فسيكون الطرف الأول مسؤولاً وحده عن ذلك في مواجهة الطرف الثاني والغير.</span></p><p><span style=\"font-size: 14px;\">3- يلتزم الطرف الأول بتأمين حاوية مقاس ( 1100 Liter ) عدد (1) ما بداخل الحاوية فقط.</span></p><p><span style=\"font-size: 14px;\">4- يتعهد الطرف الأول و يضمن نظافة المكان الذي تقع فيه الحاوية عند عمليات التفريغ والنقل والترحيل وأن تكون جميع الإجراءات المتخذة من قبلة صحيحة ونظامية.</span></p><p><span style=\"font-size: 14px;\">5- في حال كان مكان تواجد الحاوية قريباً من السيارات أو من ممتلكات الطرف الثاني ، فإن الطرف الأول يتعهد بأن تأمين مركباته و معداته ساري و سيتم تقديم المطالبة التأمينة من قبله ومتابعتها إلى حين تحصيل المبلغ المالي، وفي حال تم رفض المطالبة التأمينة لأي سبب من قبل شركة التأمين فإن الطرف الأول يعد مسؤولاً عن سداد قيمة الأضرار والتلفيات بالكامل نتيجة الخطأ المرتكب وذلك بناءً على إنعقاد العلاقة السببية فيما بين الضرر المترتب على الطرف الثاني والخطأ الصادر من الطرف الأول.</span></p><p><span style=\"font-size: 14px;\">6- يلتزم الطرف الأول ويتعهد بترحيل ونقل جميع النقابات إلى الأماكن المخصصة لذلك، ويتحمل كامل المسؤولية في حال تم تفريغها في مكان غير مخصص أو غير نظامي.</span></p><p><span style=\"font-size: 14px;\">7- يلتزم الطرف الأول ويتعهد بتحمل جميع المخالفات التي قد ترد قبل الجهات الحكومية نتيجة عدم اتباع الطرف الأول للتعليمات والأنظمة المعمول بها في المملكة العربية السعودية والمتعلقة بهذه الإتفاقية.</span></p><p><span style=\"font-size: 14px;\">8- في حال تخلف الطرف الأول عن الحضور دون إشعار الطرف الثاني على سبب مشروع و مبرر وترتب على تخلفه عدم تنفيذ الإلتزامات الواجبة عليه فيتعهد الطرف الأول بمنح الطرف الثاني ثلاث زيارات مجانية لأي زيارة يتم التخلف عنها دون سبب مبرر و مشروع ، كما يتحمل الطرف الأول كافة الغرامات التي قد يتم إصدارها من الجهات المعنية نتيجة ذلك.</span></p><p><span style=\"font-size: 14px;\">9- يلتزم الطرف الثاني يتسهيل عملية تسليم النقابات إلى الطرف الأول خلال ساعات العمل وطوال أيام الأسبوع.</span></p><p><span style=\"font-size: 14px;\">10- يلتزم الطرف الثاني بوضع ما ينتجه من نفايات في أكياس محكمة وربطها جيدا لمنع تسرب المواد السائلة عند وضعها أو نقلها، وذلك حفاظا على النظافة العامة وصحة البيئة</span></p>','2025-12-08 22:52:14','2026-01-04 12:15:27'),(15,'البند الرابع إنهاء العقد و إنتهائه :','text','<p><strong style=\"font-size: 14px;\"><u>البند الرابع إنهاء العقد و إنتهائه :</u></strong></p><p><span style=\"font-size: 14px;\">1 ينتهي هذا العقد بإنتهاء مدنه المشار لها في البند الثالث</span></p><p><span style=\"font-size: 14px;\">2 ينتهي هذا العقد في حال مخالفة الطرف الأول لأيا من التزامله المشار لها في البند الأول .</span></p><p><span style=\"font-size: 14px;\">3 ينتهي هذا العقد في أي وقت ولأي سبب كان من قبل أيا من الأطراف دون اعتراض أي طرف، على أن يتم إشعار الطرف الآخر بذلك قبل 30 يوم من تاريخ الإنهاء الفعلي.</span></p>','2025-12-10 19:01:06','2026-01-04 12:16:05'),(17,'البند الخامس تسوية المنازعات والحية المختصة','text','<p><strong style=\"font-size: 14px;\"><u>البند الخامس تسوية المنازعات والحية المختصة :</u></strong></p><p><span style=\"font-size: 14px;\">﻿﻿أي منازعات أو خلافات تنشأ لا قدر الله بين الطرفان حول هذا اله السير سوده فإن الطرفان يلتزمان بذل كافة المساعي الودية لحالها خلال 7 ايام من تاريخ إخطار الطرف الآخر بالقراع ، وإذا تعذر حلها وديات القانون إلى المحاكم الشرعية في المملكة العربية السعودية وينعقد اختصاصها المكاني في مدينة الرياض</span></p>','2025-12-10 19:07:28','2026-01-04 12:17:55'),(18,'البند السادس : أحكام عامة','text','<p><strong style=\"font-size: 14px;\"><u>البند السادس : أحكام عامة</u></strong></p><p><span style=\"font-size: 14px;\">1- يجب على الطرف الثاني ابلاغ الطرف الأول بأي ملاحظات عن العمل بموجب إشعار كتابي يرسل على إحدى العناوين السابقة .</span></p><p><span style=\"font-size: 14px;\">2- يكون سداد المبالغ المالية بين الطرفين بسندات رسمية للمؤس وبلاك ذلك فإن الطرف الأول غير مسؤول عن ذلك</span></p><p><span style=\"font-size: 14px;\">3- يتم تفسير وتطبيق هذا العقد وفقاً للقوانين والأنظمة السارية الملكة العربية السعودية</span></p><p><span style=\"font-size: 14px;\">4- لا يجوز تعديل هذا العقد واعتماد التعديلات إلا بعد موافقة الذين كتابة على هذه التعديلات</span></p><p><span style=\"font-size: 14px;\">5- يعتبر هذا العقد شامل ووالي لجميع ما تم الاتفاق عليه بين الطرق وتلعي ما سبقه من مراسلات أو اتفاقيات سواء شفهياً أو كتابياً</span></p><p><span style=\"font-size: 14px;\">6- من المعلوم لدى الطرفين أن العلاقة بينهما في علاقة تقديم حدث ولا تشكل أية شراكة أو وكالة أو علاقة عمالية ولا يعتبر أي من الطرفين ممثلا أو وكيلا قانونيا أو تجارياً أو خلافه عن الطرف الآخر ومن لا يحق لأي من الطرفين الإدعاء بأي مما سبق أو تقديم أية التزامات أو لحيدات نيابة عن الطرف الآخر، كما لا يحق لأي طرف استخدام شعارات الطرف الآخر دون أخذ الموافقة الخطية على ذلك.</span></p><p><span style=\"font-size: 14px;\">7 من المتقق عليه بين الطرفين أن العناوين الوطنية وبيانات التواصل المذكورة في مقدمة هذا العقد في العناوين المعتمد لكل مهما، وأن جميع الإخطارات والإنذارات والإشعارات المنصوص عليها في الاتفاقية يجب أن تكون كتابية وترسل من خلال هذه العناوين. وفي حال رغب أي من الطرفين في تغيير أو تعديل عنوانه، فعليه إخطار الطرف الآخر بذلك وتزويده بعنوانه الجديد فوراً من تاريخ التغيير أو التعديل، وإلا يعد جميع الإخطارات والإنذارات والإشعارات المرسلة أو المسلمة على العنوان القديم صحيحة ومنتجة لآثارها الشرعية والنظامية.</span></p><p><span style=\"font-size: 14px;\">8- يلزم هذا العقد طرقية من تاريخ توقيعها ولا يجوز لأي من الطرفين حينها التحلل من أي من أحكامه أو شروطه إلا باتفاق كتابي يصدر عن الطرفين أو من ينوب عنهما بموجب القويض موضعاً بذلك أو وكالة شرعية .</span></p><p><span style=\"font-size: 14px;\">9 عدم تعليق تنفيذ هذا العقد على أي اتفاقيات أو تعاقدات أخرى عالية أو مستقبلية بين الطرفين سواء كانت شفهياً أو كتابياً.</span></p><p><span style=\"font-size: 14px;\">10- تم تحرير هذا العقد من نسختين لكل من الطرفين نسخة، ولا يعتمد إلا بالختم ولكم جزيل الشكر.</span></p><p><br></p><p class=\"ql-align-center\"><strong style=\"font-size: 14px;\">والله الموفق ,,,</strong></p>','2025-12-10 19:08:19','2026-01-04 12:18:12'),(22,'الطرف الأول :','text','<p class=\"ql-align-justify\"><strong style=\"font-size: 14px;\">الطرف الأول/</strong><span style=\"font-size: 14px;\"> </span><strong style=\"font-size: 14px;\">مؤسسة بفت للمقاولات </strong><span style=\"font-size: 14px;\">وجنسيتها سعودية، بموجب سجل التجاري رقم (5850026989) صادر من ابهاء بتاريخ 16/05/1427هـ، الرقم الضريبي (300637265300003)، وعنوانها في مدينة خميس مشيط (حجلة) إشارة باحص خلف مجموعة الجربوع، ص.ب 6454 الرمز البريدي 62561، البريد الإلكتروني (BAFT-EST@HOTMAIL.COM)، هاتف رقم:017-2278666 تحويلة (105)، جوال (  0552200931  ) ويمثله في التوقيع على هذا العقد</span><strong style=\"font-size: 14px;\"> الأستاذ/ظافر بن عبدالله محمد الشهري بصفته (المدير العام) –</strong><span style=\"font-size: 14px;\">ويُشار اليه في هذا العقد بــ</span><strong style=\"font-size: 14px;\"> (الطرف الأول)</strong></p>','2026-01-03 07:04:17','2026-01-04 12:15:00');
